SAMPLE_RATE = 44100 

figure, axis = plt.subplots(3, 3, figsize=(15, 8))

def show_data(sig,i):
    y = np.array(sig, dtype=np.float64)
    norm = np.int16((y / y.max()) * 32767)
    N = norm.size
    
    yf = rfft(norm)
    xf = rfftfreq(N, 1 / SAMPLE_RATE)
    
    axis[i,0].plot(xf, np.abs(yf))
    axis[i,0].set_title('FFT')
    axis[i,0].get_xaxis().set_visible(False)
    axis[i,0].get_yaxis().set_visible(False)
    
    bank = logfbank(y, SAMPLE_RATE, nfilt=26, nfft=1103).T
    axis[i,1].imshow(bank, cmap='hot', origin='lower', aspect='auto')
    axis[i,1].set_title('filter bank')
    axis[i,1].get_xaxis().set_visible(False)
    axis[i,1].get_yaxis().set_visible(False)
    
    mfccs = mfcc(y, SAMPLE_RATE, numcep=13, nfilt=26, nfft=1103).T
    axis[i,2].imshow(mfccs, cmap='hot', origin='lower', aspect='auto')
    axis[i,2].set_title('mfcc')
    axis[i,2].get_xaxis().set_visible(False)
    axis[i,2].get_yaxis().set_visible(False)
    
    
  
show_data(lowFreqTest,0)
show_data(midFreqTest,1)
show_data(highFreqTest,2)

plt.show()
